def label(colors):
    # Initialize the resistance value to 0
    resistance = 0

    # Iterate through the colors and add the value of each color to the resistance
    for color in colors:
        if color == "black":
            resistance += 0
        elif color == "brown":
            resistance += 1
        elif color == "red":
            resistance += 2
        elif color == "orange":
            resistance += 3
        elif color == "yellow":
            resistance += 4
        elif color == "green":
            resistance += 5
        elif color == "blue":
            resistance += 6
        elif color == "violet":
            resistance += 7
        elif color == "grey":
            resistance += 8
        elif color == "white":
            resistance += 9

    # Return the resistance value in ohms
    return f"{resistance} ohms"

