class Scale:
    def __init__(self, tonic):
        self.tonic = tonic

    def chromatic(self):
        return [self.tonic + i for i in range(12)]

    def interval(self, intervals):
        scale = [self.tonic]
        for interval in intervals:
            scale.append(scale[-1] + interval)
        return scale
