def label(colors):
    resistance = {"black": 0, "brown": 1, "red": 2, "orange": 3, "yellow": 4, "green": 5, "blue": 6, "violet": 7, "grey": 8, "white": 9}

    def get_exponent(num_zeros):
        if num_zeros == 0:
            return ""
        elif num_zeros == 1:
            return "kilo"
        else:
            return f"mega({num_zeros})"

    def mega(n):
        if n == 2:
            return "megaohm"
        else:
            return f"{get_exponent(n)}ohms"

    main_value = (resistance[colors[0]] * 10) + resistance[colors[1]]
    exponent = len(colors) - 2
    prefix = get_exponent(exponent)
    return f"{main_value}{prefix}"

# Test cases
